grant execute on [dbo].[psa_hash] to [psa_etl_manager];
grant execute on [dbo].[psa_hash] to [psa_owner];

if object_id('[dbo].[sp_manage_table_properties]',N'P') is not null drop procedure [dbo].[sp_manage_table_properties];

exec sys.sp_executesql
N'
create procedure [dbo].[sp_manage_table_properties]
 (
   @domain_name sysname,
   @property sysname,
   @property_value sql_variant
 ) with encryption
as

set nocount on;

declare
   @object_id int;

select
   @object_id=object_id(@domain_name);

if @object_id is null begin;
   print N''Domain ''+isnull(@domain_name,N''{NULL}'')+N'' does not exist.  Property was not added.''
   return 1;
end;

declare
   @object_schema sysname,
   @object_name sysname;

select
   @object_schema=object_schema_name(@object_id),
   @object_name=object_name(@object_id);

if exists(select 1 from sys.extended_properties  where [major_id]=@object_id and [class]=1 and [minor_id]=0 and [name]=@property) begin;
   exec sys.sp_updateextendedproperty
      @name=@property,
      @value=@property_value,

      @level0type=N''SCHEMA'',
      @level0name=@object_schema,

      @level1type=N''TABLE'',
      @level1name=@object_name;
end else begin;
   exec sys.sp_addextendedproperty
      @name=@property,
      @value=@property_value,

      @level0type=N''SCHEMA'',
      @level0name=@object_schema,

      @level1type=N''TABLE'',
      @level1name=@object_name;
end;
return 0;
';

exec sys.sp_MS_marksystemobject 'sp_manage_table_properties';

if object_id('[dbo].[sp_manage_column_properties]',N'P') is not null drop procedure [dbo].[sp_manage_column_properties];

exec sys.sp_executesql
N'
create procedure [dbo].[sp_manage_column_properties]
 (
   @domain_name sysname,
   @column_name sysname,
   @property sysname,
   @property_value sql_variant
 ) with encryption
as

set nocount on;

declare
   @object_id int,
   @column_id int;

select
   @object_id=object_id(@domain_name);

if @object_id is null begin;
   print N''Domain ''+isnull(@domain_name,N''{NULL}'')+N'' does not exist.  Property was not added.''
   return 1;
end;

declare
   @object_schema sysname,
   @object_name sysname;

select
   @object_schema=object_schema_name(@object_id),
   @object_name=object_name(@object_id);

select 
   @column_id=[column_id] 
from 
   sys.columns 
where 
   [object_id]=@object_id 
   and 
   [name]=@column_name;

if @column_id is null begin;
   print N''Attribute ''+isnull(@column_name,N''{NULL}'')+N'' does not exist.  Property was not added.''
   return 1;
end;

if exists(select 1 from sys.extended_properties  where [major_id]=@object_id and [class]=1 and [minor_id]=@column_id and [name]=@property) begin;
   exec sys.sp_updateextendedproperty
      @name=@property,
      @value=@property_value,

      @level0type=N''SCHEMA'',
      @level0name=@object_schema,

      @level1type=N''TABLE'',
      @level1name=@object_name,

      @level2type=N''COLUMN'',
      @level2name=@column_name;

end else begin;
   exec sys.sp_addextendedproperty
      @name=@property,
      @value=@property_value,

      @level0type=N''SCHEMA'',
      @level0name=@object_schema,

      @level1type=N''TABLE'',
      @level1name=@object_name,

      @level2type=N''COLUMN'',
      @level2name=@column_name;
end;

return 0;
';

exec sys.sp_MS_marksystemobject 'sp_manage_column_properties';

set nocount on;
set quoted_identifier on;
set ansi_nulls on;

