set nocount on;
declare @ts datetime2(7)=sysutcdatetime(); declare @batch uniqueidentifier=convert(uniqueidentifier,context_info());
with [delete_source] as ( select {{{columnset}}} @batch [psa_batch_id] from [deleted] i ) update {{{domain}}} set [psa_entity_sequence]=t.[psa_entity_sequence]+1, [psa_active_state]=0, [psa_start_period]=@ts, [psa_batch_id]=s.[psa_batch_id], [psa_hash_id]=0x from [delete_source] s inner join {{{domain}}} t on t.[psa_active_state]=1 and {{{joinset}}}
end;

