as
-- do not alter/add/extend this trigger, or any psa trigger
-- authored by slalom consulting 2014
begin;

   set nocount on;

   declare @ts datetime2(7)=sysutcdatetime();
   declare @batch uniqueidentifier=convert(uniqueidentifier,context_info());

   insert {{{domain}}}
    (
{{{columnset}}}
