end try begin catch;
declare @ErrorMessage nvarchar(4000)=error_message(); declare @ErrorSeverity int=error_severity(); declare @ErrorState int=error_state(); raiserror(@ErrorMessage,@ErrorSeverity,@ErrorState) with log; end catch;

